        <script>
            (function() {
                'use strict';
                var openNavIcon = document.querySelector('.nav__open');
                var closeNavIcon = document.querySelector('.nav__close');
                var closeNavLinks = document.querySelectorAll('.nav__wrapper ul li a');

                openNavIcon.addEventListener('click', toggleNav);
                closeNavIcon.addEventListener('click', toggleNav);

                function toggleNav() {
                    var body = document.body;
                    var className = 'nav--open';

                    var classes = body.className.split(' ');
                    var existingIndex = classes.indexOf(className);

                    if (existingIndex >= 0) {
                        classes.splice(existingIndex, 1);
                        closeNavLinks.forEach(function(link) {
                            link.removeEventListener('click', toggleNav);
                        });
                    } else {
                        classes.push(className);
                        closeNavLinks.forEach(function(link) {
                            link.addEventListener('click', toggleNav);
                        });
                    }

                    body.className = classes.join(' ');
                }
            })();
        </script>
